import React, { Component } from 'react';
import './App.css';
import 'semantic-ui/dist/semantic.min.css';
import { Route, Switch} from 'react-router-dom'
import LandingPage from './MainPages/Landing'
import AboutMe from './MainPages/AboutMe'
import Projects from './MainPages/Projects'
import DisplayProject from './MainPages/DisplayProject'
import NoRoute from './MainPages/NoRoute'
import AllProjects from './proj-data'


class App extends Component {
  state={
    projects: AllProjects
  }
  // <Route exact path="/project/1"
  // render={(props) => <DisplayProject {...props} location={this.state.projects[0]} />} />
  // <Route exact path="/project/2" location={this.state.projects[1]} component={DisplayProject} />
  // <Route exact path="/project/3" location={this.state.projects[2]} component={DisplayProject} />
  render() {

    return (
      <div className="App">
        <LandingPage />
        <Switch>
            <Route exact path="/" component={LandingPage} />
            <Route path ="/bio" component={AboutMe} />
            <Route path="/projects" component={Projects} />
            <Route component={NoRoute} />
        </Switch>
      </div>
    );
  }
}

export default App;
